fields.formula:
if( 
  and( 
    field("Risk rating raw") > 0, 
    field("Risk rating raw") < 4 
  ), 
  "🔷 Minimal", 
  if( 
    and( 
        field("Risk rating raw") >= 4, 
        field("Risk rating raw") < 10 
    ), 
    "🔔 Moderate", 
    if( 
      and( 
        field("Risk rating raw") >= 10, 
        field("Risk rating raw") < 16 
      ), 
      "⚠️ Severe", 
      if( 
        field("Risk rating raw") >= 16, 
        "🛑 Critical", 
        "" 
      ) 
    ) 
  ) 
)

if( 
  and( 
    field("Residual rating raw") > 0, 
    field("Residual rating raw") < 4 
  ), 
  "🔷 Minimal", 
  if( 
    and( 
        field("Residual rating raw") >= 4, 
        field("Residual rating raw") < 10 
    ), 
    "🔔 Moderate", 
    if( 
      and( 
        field("Residual rating raw") >= 10, 
        field("Residual rating raw") < 16 
      ), 
      "⚠️ Severe", 
      if( 
        field("Residual rating raw") >= 16, 
        "🛑 Critical", 
        if( 
          field("Risk rating raw") = 0, 
          "", 
          "✅ No risk" 
        ) 
      ) 
    ) 
  ) 
)

multiply(
  if(
    totext(field("Probability")) = "Rare", 
    1, 
    if(
      totext(field("Probability")) = "Unlikely", 
      2, 
      if(
        totext(field("Probability")) = "Possible", 
        3, 
        if(
          totext(field("Probability")) = "Probable", 
          4, 
          if(
            totext(field("Probability")) = "Almost certain", 
            5, 
            0 
          ) 
        ) 
      ) 
    ) 
  ), 
  if(
    totext(field("Impact")) = "Very low", 
    1, 
    if(
      totext(field("Impact")) = "Low", 
      2, 
      if(
        totext(field("Impact")) = "Medium", 
        3, 
        if(
          totext(field("Impact")) = "High", 
          4, 
          if(
            totext(field("Impact")) = "Very high", 
            5, 
            0 
          ) 
        ) 
      ) 
    ) 
  ) 
)

minus( 
  field("Risk rating raw"), 
  multiply( 
    field("Risk rating raw"), 
    divide( 
      when_empty( 
        field("Expected success rate (%)"), 
        0
      ), 
      100
    ) 
  ) 
)

count(
  filter(
    lookup("Related risks", "Risk rating raw"), 
    lookup("Related risks", "Risk rating raw") >= 16
  )
)

count(
  filter(
    lookup("Related risks", "Risk rating raw"), 
    and( 
        lookup("Related risks", "Risk rating raw") >= 10, 
        lookup("Related risks", "Risk rating raw") < 16 
    ) 
  )
)

